    public static void checkID(List<String> list, String response) {

        countNum++;

        JSONObject jsonObject = new JSONObject(response);

        JSONArray jsonArray = new JSONArray(jsonObject.get("posts").toString());

        int size = jsonArray.length();

        for (int i = 0; i < size; i++) {

            jsonObject = jsonArray.getJSONObject(i);

            String id = jsonObject.getJSONObject("post").get("id").toString();

            if (list.contains(id)) {
                if (!isRepeat) {
//                    System.out.println(list);
                    isRepeat = true;
                }
                System.out.println("ID:" + id + "重复请检查，(当前请求次数：" + countNum + ")");
            } else {
                list.add(id);
            }
        }
        System.out.println(list);
        isRepeat = false;
    }
